$(document).ready(function(){$(".left").hover(function(){$("#left_img").attr("src","images/arrow-gray-left.png")},function(){$("#left_img").attr("src","images/arrow-blue-left.png")}),$(".right").hover(function(){$("#right_img").attr("src","images/arrow-gray-right.png")},function(){$("#right_img").attr("src","images/arrow-blue-right.png")}),$("li").each(function(i){i<$("#slider li").length/2?$(this).addClass("slider-row-one-image"):$(this).addClass("slider-two-one-image")});var i=$("#slider");i.find(".slider-row-one-image").wrapAll('<div class="slider-row-one" />'),i.find(".slider-two-one-image").wrapAll('<div class="slider-row-two" />');var t=$("#slider").find(".slider-row-one"),e=$("#slider").find(".slider-row-two");i.find(".left").click(function(){t.animate({left:-t.find("li:last").width()},100,function(){t.append(t.find("li:first")).css("left","0")}),e.animate({left:-e.find("li:last").width()},100,function(){e.append(e.find("li:first")).css("left","0")})}),i.find(".right").click(function(){t.animate({left:t.find("li:last").width()},100,function(){t.prepend(t.find("li:last")).css("left","0")}),e.animate({left:e.find("li:last").width()},100,function(){e.prepend(e.find("li:last")).css("left","0")})})});